DROP TABLE IF EXISTS `p_banmian`;
CREATE TABLE `p_banmian` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '版面id',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '报刊期刊id',
  `btitle` varchar(225) NOT NULL COMMENT '版面标题',
  `pdf` varchar(225) DEFAULT NULL COMMENT 'pdf路径',
  `bg` varchar(225) NOT NULL COMMENT '清晰版面背景图',
  `bgthumb` varchar(255) DEFAULT NULL COMMENT '略缩版面背景图',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

